import React, { useEffect, useState } from "react";
import styled from "styled-components";
import OverviewComponent from "./OverviewComponent";
import Transaction from "./Transaction";
import Header from './Header';
import axios from 'axios';

const Container = styled.div`
  background-color: white;
  color: #0d1d2c;
  display: flex;
  flex-direction: column;
  padding: 10px 22px;
  font-size: 18px;
  width: 360px;
  align-items: center;
  justify-content: space-between;
`;

const HomeComponent = (props) => {
    const [transactions, updateTransaction] = useState([]);
    const [expense, updateExpense] = useState(0);
    const [income, updateIncome] = useState(0);

    const calculateBalance = () => {
        let exp = 0;
        let inc = 0;
        transactions.forEach(payload => {
            if (payload.type === "EXPENSE") {
                exp += payload.amount;
            } else {
                inc += payload.amount;
            }
        });
        updateExpense(exp);
        updateIncome(inc);
    };

    useEffect(() => {
        calculateBalance();
        // Fetch existing transactions from Firebase
        axios.get("https://expense-tracker-fe43e-default-rtdb.firebaseio.com/expenseData.json")
            .then((res) => {
                const fetchedTransactions = [];
                for (const key in res.data) {
                    fetchedTransactions.push({
                        ...res.data[key],
                        id: key
                    });
                }
                updateTransaction(fetchedTransactions);
            })
            .catch((err) => {
                console.error("Error fetching transactions:", err);
            });
    }, []);

    const addTransaction = (payload) => {
        const transactionArray = [...transactions];
        transactionArray.push(payload);
        updateTransaction(transactionArray);

        // Store new transaction in Firebase
        axios.post("https://expense-tracker-fe43e-default-rtdb.firebaseio.com/expenseData.json", payload)
            .then((res) => {
                if (res.status === 200) {
                    console.log("Transaction added successfully!");
                }
            })
            .catch((err) => {
                console.error("Error adding transaction:", err);
            });
    };

    return (
        <Container>
            <Header />
            <OverviewComponent
                expense={expense}
                income={income}
                addTransaction={addTransaction}
            />
            {transactions.length > 0 ? (
                <Transaction transactions={transactions} />
            ) : (
                ""
            )}
        </Container>
    );
};

export default HomeComponent;
