import styled from 'styled-components';
import React, { useState } from 'react';
import { Container, Row, Col, Form, Button, Alert } from "react-bootstrap";
//import { useNavigate } from "react-router-dom";
//import AuthContext from './auth-context';
import HomeComponent from './HomeComponent';

const FormContainer = styled.div`
display: flex;
flex-direction: column;
border: 1px solid #e6e8e9;
gap: 10px;
width: 100%;
padding: 15px 20px;
margin: 20px;
& input{
    outline: none;
    padding: 10px 12px;
    border-radius: 4px;
    border: 1px solid #e6e8e9;
}
`;

const AuthForm = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [showSignUp, setShowSignUp] = useState(true);
  const [error, setError] = useState(null);
  //const navigate = useNavigate();
//   const authCtx = useContext(AuthContext);
//   const isLoggedIn = authCtx.isLoggedIn;

  const handleLogin = (e) => {
    e.preventDefault();
    const storedEmail = localStorage.getItem("userEmail");
    const storedPassword = localStorage.getItem("userPassword");

    if (email === storedEmail && password === storedPassword) {
    //   navigate("/"); 
    //   authCtx.login();
    //return (<HomeComponent/>);
    console.log("Login successful.");
    } else {
      setError("Invalid credentials. Please try again.");
    }
  };

  const handleSignup = (e) => {
    e.preventDefault();
    if(password === confirmPassword){
        localStorage.setItem("userPassword", password);
        localStorage.setItem("userEmail", email);
        setShowSignUp(false);
    }
    else{
        setError("Please enter password correctly!");
    }

  };

  return (
    <FormContainer>
       <Container>
      <Row className="justify-content-center">
        <Col md={6}>
          <div>{showSignUp && <h4>Sign UP</h4>}</div>
          <div>{!showSignUp && <h4>Login</h4>}</div>
          {error && <Alert variant="danger">{error}</Alert>}
          <Form onSubmit={handleLogin}>
            <Form.Group controlId="email">
              <Form.Label>Email:</Form.Label>
              <Form.Control
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </Form.Group>

            <Form.Group controlId="password">
              <Form.Label>Password:</Form.Label>
              <Form.Control
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </Form.Group>
            {showSignUp &&   <Form.Group controlId="password">
              <Form.Label>Confirm Password:</Form.Label>
              <Form.Control
                type="password"
                value={confirmPassword}
                onChange={(e) => setConfirmPassword(e.target.value)}
                required
              />
            </Form.Group>}
            {showSignUp ? (
              <Button variant="primary" type="submit" onClick={handleSignup}> {/* Add onClick to call handleSignup */}
                Sign Up
              </Button>
            ) : (
              <Button variant="primary" type="submit" onClick={handleLogin}>
                Login
              </Button>
            )}
          </Form>
        </Col>
      </Row>
    </Container>
    </FormContainer>
  );
};

export default AuthForm;
