// AuthForm.js
import React, { useState } from 'react';

const AuthForm = ({ onSignUp, onLogin }) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');

  const handleSignUp = () => {
    // Perform validation and sign-up logic here
    if (password === confirmPassword && email && password) {
      onSignUp(email, password);
    } else {
      alert('Please enter valid email and matching passwords.');
    }
  };

  const handleLogin = () => {
    // Perform login logic here
    // You should implement proper authentication and validation here.
    onLogin(email, password);
  };

  return (
    <div>
      <h2>{onSignUp ? 'Sign Up' : 'Login'}</h2>
      <label>Email:</label>
      <input type="text" value={email} onChange={(e) => setEmail(e.target.value)} />
      <label>Password:</label>
      <input type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
      {onSignUp && (
        <>
          <label>Confirm Password:</label>
          <input
            type="password"
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
          />
        </>
      )}
      {onSignUp ? (
        <button onClick={handleSignUp}>Sign Up</button>
      ) : (
        <button onClick={handleLogin}>Login</button>
      )}
    </div>
  );
};

export default AuthForm;
