import styled from 'styled-components';
import { useState, useEffect } from 'react';

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 10px 22px;
  font-size: 18px;
  width: 100%;
  gap: 10px;
  font-weight: bold;

  & input {
    outline: none;
    padding: 10px 12px;
    border-radius: 12px;
    border: 1px solid #e6e8e9;
    background: #e6e8e9;
    width: 100%;
  }
`;

const Cell = styled.div`
  display: flex;
  flex-direction: row;
  padding: 10px 15px;
  font-size: 14px;
  border-radius: 2px;
  align-items: center;
  width: 100%;
  border: 1px solid #e6e8e9;
  font-weight: normal;
  justify-content: space-between;
  border-right: 4px solid ${(props) => (props.isExpense ? 'red' : 'green')};
`;

const TransactionCell = (props) => {
  return (
    <Cell isExpense={props.payload?.type === 'EXPENSE'}>
      <span>{props.payload.desc}</span>
      <span>${props.payload.amount}</span>
    </Cell>
  );
};

const Transaction = (props) => {
  const [filteredTxn, setFilteredTxn] = useState([]);
  const [searchText, setSearchText] = useState('');

  const filteredData = (searchValue) => {
    if (!searchValue || !searchValue.trim().length) {
      setFilteredTxn(props.transactions);
      return;
    }

    const filteredTransactions = props.transactions.filter((payload) =>
      payload.desc.toLowerCase().includes(searchValue.toLowerCase().trim())
    );
    setFilteredTxn(filteredTransactions);
  };

  useEffect(() => {
    const storedTransactions = JSON.parse(localStorage.getItem('transactions'));
    if (storedTransactions) {
      setFilteredTxn(storedTransactions);
    } else {
      setFilteredTxn(props.transactions);
    }
  }, []);

  useEffect(() => {
    localStorage.setItem('transactions', JSON.stringify(filteredTxn));
  }, [filteredTxn]);

  return (
    <Container>
      Transactions
      <input
        placeholder="Search"
        value={searchText}
        onChange={(e) => {
          setSearchText(e.target.value);
          filteredData(e.target.value);
        }}
      />
      {filteredTxn.length ? (
        filteredTxn.map((payload) => <TransactionCell payload={payload} />)
      ) : (
        <span>No transactions found.</span>
      )}
    </Container>
  );
};

export default Transaction;
