import styled from 'styled-components';
import React, { useState} from 'react';
import { Container, Row, Col, Form, Button, Alert } from "react-bootstrap";
import { auth } from '../Config/Firebase';
import { useNavigate } from "react-router-dom";
//import AuthContext from './auth-context';
//import HomeComponent from './HomeComponent';
import { getAuth, createUserWithEmailAndPassword } from "firebase/auth";
import { signInWithEmailAndPassword } from "firebase/auth";
import { getDatabase } from "firebase/database";

const FormContainer = styled.div`
display: flex;
flex-direction: column;
border: 1px solid #e6e8e9;
gap: 10px;
width: 100%;
padding: 15px 20px;
margin: 20px;
& input{
    outline: none;
    padding: 10px 12px;
    border-radius: 4px;
    border: 1px solid #e6e8e9;
}
`;

const AuthForm = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  //const [confirmPassword, setConfirmPassword] = useState('');
  const [showSignUp, setShowSignUp] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();
  //const authCtx = useContext(AuthContext);
  //const isLoggedIn = authCtx.isLoggedIn;
  const db = getDatabase();

  const handleLogin = () => {
    signInWithEmailAndPassword(auth, email, password)
    .then((userCredential) => {
      // Signed in 
      const user = userCredential.user;
      navigate("/home");
    })
    .catch((error) => {
      const errorCode = error.code;
      const errorMessage = error.message;
    });

  };

  const handleSignup = () => {
    createUserWithEmailAndPassword(auth, email, password)
  .then((success) => {
    // Signed in 
    //const user = userCredential.user;
    set(ref(db, `users/${success.user.uid}`), {
      email: email,
      id: success.user.uid
    });
  })
  .catch((error) => {
    const errorCode = error.code;
    const errorMessage = error.message;
    // ..
  });

    setShowSignUp(!showSignUp);


  };

  return (
    <FormContainer>
       <Container>
      <Row className="justify-content-center">
        <Col md={6}>
          <div>{showSignUp? "Sign Up": "Login"}</div>
          {error && <Alert variant="danger">{error}</Alert>}
          <Form action="#">
            <Form.Group controlId="email">
              <Form.Label>Email:</Form.Label>
              <Form.Control
                type="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                required
              />
            </Form.Group>

            <Form.Group controlId="password">
              <Form.Label>Password:</Form.Label>
              <Form.Control
                type="password"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
                required
              />
            </Form.Group>
            {showSignUp ? (
              <Button variant="primary" type="submit" onClick={handleSignup}> {/* Add onClick to call handleSignup */}
                Sign Up
              </Button>
            ) : (
              <Button variant="primary" type="submit" onClick={handleLogin}>
                Login
              </Button>
            )}
          </Form>
          <Button variant="primary" type="button" onClick={(e) => setShowSignUp(!showSignUp)}>
                {showSignUp? "Already have an Account? Login":"Don't have an account? SignUp!"}
              </Button>
        </Col>
      </Row>
    </Container>
    </FormContainer>
  );
};

export default AuthForm;
