import styled from 'styled-components';
import { useState, useEffect } from 'react';

const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  margin: 10px;
  width: 100%;
`;

const BalanceBox = styled.div`
  display: flex;
  flex-direction: row;
  justify-content: space-between;
  align-items: center;
  font-size: 18px;
  width: 100%;
  font-weight: bold;
`;

const AddTransaction = styled.button`
  background: black;
  color: white;
  padding: 5px 10px;
  border-radius: 4px;
  cursor: pointer;
  font-weight: bold;
  font-size: 15px;
  justify-content: space-between;
`;

const AddTransactionContainer = styled.div`
  display: flex;
  flex-direction: column;
  border: 1px solid #e6e8e9;
  gap: 10px;
  width: 100%;
  padding: 15px 20px;
  margin: 20px;
  & input {
    outline: none;
    padding: 10px 12px;
    border-radius: 4px;
    border: 1px solid #e6e8e9;
  }
`;

const RadioBox = styled.div`
  display: flex;
  flex-direction: row;
  width: 100%;
  align-items: center;
  & input {
    width: unset;
    margin: 0 10px;
  }
`;

const ExpenseContainer = styled.div`
  display: flex;
  flex-direction: row;
  gap: 12px;
  margin: 20px;
`;

const ExpenseBox = styled.div`
  display: flex;
  flex-direction: column;
  border-radius: 4px;
  border: 1px solid #e6e8e9;
  padding: 15px 20px;
  width: 125px;
  font-size: 14px;
  & span {
    font-weight: bold;
    font-size: 20px;
    color: ${(props) => (props.isIncome ? 'green' : 'red')};
  }
`;

const AddTransactionView = (props) => {
  const [amount, setAmount] = useState('');
  const [desc, setDesc] = useState('');
  const [type, setType] = useState('EXPENSE');

  const AmountChangeHandler = (event) => {
    setAmount(event.target.value);
  };

  const DescChangeHandler = (event) => {
    setDesc(event.target.value);
  };

  const TypeChangeHandler = (event) => {
    setType(event.target.value);
  };

  const transactionHandler = () => {
    props.addTransaction({ amount: Number(amount), desc, type, id: Date.now() });
    setAmount('');
    setDesc('');
    setType('EXPENSE');
    props.setAddTransaction();
  };

  return (
    <AddTransactionContainer>
      <input placeholder="Amount" value={amount} onChange={AmountChangeHandler} type="number" />
      <input placeholder="Description" value={desc} onChange={DescChangeHandler} type="text" />
      <RadioBox>
        <input type="radio" id="expense" name="type" value="EXPENSE" checked={type === 'EXPENSE'} onChange={TypeChangeHandler} />
        <label htmlFor="expense">Expense</label>
        <input type="radio" id="income" name="type" value="INCOME" checked={type === 'INCOME'} onChange={TypeChangeHandler} />
        <label htmlFor="income">Income</label>
      </RadioBox>
      <AddTransaction onClick={transactionHandler}>Add Transaction</AddTransaction>
    </AddTransactionContainer>
  );
};

const OverviewComponent = (props) => {
  const [addTransaction, setAddTransaction] = useState(false);

  const AddTransactionHandler = () => {
    setAddTransaction(!addTransaction);
  };

  useEffect(() => {
    const storedIncome = localStorage.getItem('income');
    const storedExpense = localStorage.getItem('expense');

    if (storedIncome) {
      props.updateIncome(parseFloat(storedIncome));
    }
    if (storedExpense) {
      props.updateExpense(parseFloat(storedExpense));
    }
  }, []);

  useEffect(() => {
    localStorage.setItem('income', props.income);
  }, [props.income]);

  useEffect(() => {
    localStorage.setItem('expense', props.expense);
  }, [props.expense]);

  return (
    <Container>
      <BalanceBox>
        Balance ${props.income - props.expense}
        <AddTransaction onClick={AddTransactionHandler}>{addTransaction ? 'CANCEL' : 'ADD'}</AddTransaction>
      </BalanceBox>
      {addTransaction && <AddTransactionView setAddTransaction={setAddTransaction} addTransaction={props.addTransaction} />}
      <ExpenseContainer>
        <ExpenseBox isIncome={false}>
          Expense<span>${props.expense}</span>
        </ExpenseBox>
        <ExpenseBox isIncome={true}>
          Income<span>${props.income}</span>
        </ExpenseBox>
      </ExpenseContainer>
    </Container>
  );
};

export default OverviewComponent;
