import styled from 'styled-components';
import OverviewComponent from './OverviewComponent';
import Transaction from './Transaction';
import { useState, useEffect, useContext } from 'react';
import AuthForm from './AuthForm';
import AuthContext from './auth-context';
const Container = styled.div`
  display: flex;
  flex-direction: column;
  align-items: center;
  margin: 30px 0 10px;
  width: 360px;
`;

const HomeComponent = (props) => {
  const [transactions, setTransactions] = useState([]);
  const [expense, updateExpense] = useState(0);
  const [income, updateIncome] = useState(0);
  const [showSignUp, setShowSignUp] = useState(true);

  const authCtx = useContext(AuthContext);
  const isLoggedIn = authCtx.isLoggedIn;

  // Load transactions from local storage when the component mounts
  useEffect(() => {
    const savedTransactions = localStorage.getItem('transactions');
    if (savedTransactions) {
      setTransactions(JSON.parse(savedTransactions));
    }
  }, []);

  // Update balance and save transactions to local storage whenever transactions change
  useEffect(() => {
    calculateBalance();
    localStorage.setItem('transactions', JSON.stringify(transactions));
    localStorage.setItem('isLoggedIn', isLoggedIn);
  }, [transactions, isLoggedIn]);

  const addTransaction = (payload) => {
    const transactionArray = [...transactions];
    transactionArray.push(payload);
    setTransactions(transactionArray);
  };

  const calculateBalance = () => {
    let exp = 0;
    let inc = 0;
    transactions.forEach((payload) => {
      payload.type === 'EXPENSE' ? (exp += payload.amount) : (inc += payload.amount);
    });

    updateExpense(exp);
    updateIncome(inc);
  };

  const handleSignUp = (email, password) => {
    // Perform sign-up logic here and set isLoggedIn to true if successful
    // You should implement proper sign-up logic and validation here.
    setIsLoggedIn(true);
    setShowSignUp(false);
  };

  const handleLogin = (email, password) => {
    // Perform login logic here and set isLoggedIn to true if successful
    // You should implement proper login logic and validation here.
    // For demonstration purposes, we assume a hardcoded email and password for login.
    const validEmail = 'user@example.com';
    const validPassword = 'password';

    if (email === validEmail && password === validPassword) {
      setIsLoggedIn(true);
      setShowSignUp(false);
    } else {
      alert('Invalid credentials.');
    }
  };

  return (
    <Container>
      {isLoggedIn ? (
        <>
          <OverviewComponent addTransaction={addTransaction} expense={expense} income={income} />
          <Transaction transactions={transactions} />
        </>
      ) : (
        <AuthForm
         onSignUp={handleSignUp} 
         onLogin={handleLogin}
         showSignUp={showSignUp}
          setShowSignUp={setShowSignUp} />
      )}
    </Container>
  );
};

export default HomeComponent;
